```rust
use std::marker::PhantomData;

pub struct JsonlQuery<T> {
    predicates: Vec<Box<dyn Fn(&T) -> bool + Send + Sync>>,
    _phantom: PhantomData<T>,
}

impl<T> JsonlQuery<T>
where
    T: DeserializeOwned + 'static,
{
    pub fn new() -> Self {
        Self {
            predicates: Vec::new(),
            _phantom: PhantomData,
        }
    }

    pub fn filter<F>(mut self, predicate: F) -> Self
    where
        F: Fn(&T) -> bool + Send + Sync + 'static,
    {
        self.predicates.push(Box::new(predicate));
        self
    }

    fn matches(&self, value: &T) -> bool {
        self.predicates.iter().all(|pred| pred(value))
    }
}
```

```rust
impl<R: AsyncRead + Unpin> JsonlReader<R> {
    pub fn stream_resilient<T>(
        self
    ) -> (impl Stream<Item = T>, WarningCollector)
    where
        T: DeserializeOwned + 'static,
    {
        let collector = WarningCollector::new();
        let collector_clone = collector.clone();

        let stream = futures::stream::unfold(
            (self, collector_clone),
            |(mut reader, collector)| async move {
                loop {
                    match reader.read_line().await {
                        Ok(Some(value)) => return Some((value, (reader, collector))),
                        Ok(None) => return None, // EOF
                        Err(e) => {
                            // Collect warning and continue
                            collector.add(Warning::MalformedJson {
                                line_number: reader.line_number,
                                error: e.to_string(),
                            });
                            // Continue to next line
                            continue;
                        }
                    }
                }
            },
        );

        (stream, collector)
    }
}
```

