    fn check_cycle(&self, from: &IssueId, to: &IssueId) -> Result<bool> {
        let exists = sqlx::query_scalar!(
            r#"
            WITH RECURSIVE paths AS (
                SELECT issue_id, depends_on_id, 1 as depth
                FROM dependencies WHERE issue_id = $1
                UNION ALL
                SELECT d.issue_id, d.depends_on_id, p.depth + 1
                FROM dependencies d JOIN paths p 
                ON d.issue_id = p.depends_on_id
                WHERE p.depth < 100
            )
            SELECT EXISTS(SELECT 1 FROM paths WHERE depends_on_id = $2)
            "#,
            to, from
        ).fetch_one(&self.pool).await?;

        Ok(exists.unwrap_or(false))
    }

