#[tokio::test]
async fn test_set_workspace_evicts_when_full() {
    let temps: Vec<TempDir> = (0..=MAX_CACHED_WORKSPACES)
        .map(|_| TempDir::new().unwrap())
        .collect();

    // Initialize .rivets/ in each
    for temp in &temps {
        std::fs::create_dir(temp.path().join(".rivets")).unwrap();
    }

    let mut context = Context::new();

    // Fill cache to limit
    for temp in &temps[..MAX_CACHED_WORKSPACES] {
        context.set_workspace(temp.path()).await.unwrap();
    }
    assert_eq!(context.cache_size(), MAX_CACHED_WORKSPACES);

    let first_workspace = temps[0].path().canonicalize().unwrap();

    // Add one more - should trigger eviction
    context.set_workspace(temps[MAX_CACHED_WORKSPACES].path()).await.unwrap();

    assert_eq!(context.cache_size(), MAX_CACHED_WORKSPACES);
    assert!(!context.storage_cache.contains_key(&first_workspace));
}
